import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
  // const [red, setRed] = useState();
  // const [yellow, setYellow] = useState();
  // const [green, setGreen] = useState();

  const config = {
    red: {
      backgroundColor: "red",
      duration: 2000,
      next: "yell",
    },
    yellow: {
      backgroundColor: "yellow",
      duration: 2000,
      next: "red",
    },
    green: {
      backgroundColor: "green",
      duration: 2000,
      next: "yellow",
    },
  };

  function App({ backgroundColor }) {

    return <div className="circle" style={{position: "relative", backgroundColor }} />;
  }
  const initialColor = "green";

  const [currentColor, setCurrentColor] = useState(initialColor);

  useEffect(() => {
    const { duration, next } = config[currentColor];

    const timerId = setTimeout(() => {
      setCurrentColor(next);
    }, duration);

    return () => {
      clearTimeout(timerId);
    };
  }, [currentColor]);

  return (
    <div className="App">
      {/* <div className="outer-box">
        <div className="blackbox">
          <div className="row">
            <div className="circle" style={{ position: "relative" }}></div>
          </div> */}

      {/* <div className="row">
            <div className="circle" style={{ position: "relative" }}></div>
          </div>
          <div className="row">
            <div className="circle"></div>
          </div> */}
      {/* </div>
      </div> */}

      <div className={["outer-box"].filter((colors) => !!colors).join(" ")}>
        <div className="blackbox">
          {Object.keys(config).map((color) => (
            <App
              key={color}
              backgroundColor={
                color === currentColor
                  ? config[color].backgroundColor
                  : undefined
              }
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
